def binary_xor(bin_str1, bin_str2):
    max_len = max(len(bin_str1), len(bin_str2))
    bin_str1 = bin_str1.zfill(max_len)
    bin_str2 = bin_str2.zfill(max_len)

    xor_result = ''
    for i in range(max_len):
        if bin_str1[i] != bin_str2[i]:
            xor_result += '1'
        else:
            xor_result += '0'

    return xor_result
