def interval_intersection(interval1, interval2):
	# sort the intervals so the left one is always before the right one
	interval1, interval2 = sorted([interval1, interval2])

	# compare the end of the first interval with the start of the second
	if interval1[1] < interval2[0] or interval1[1] == interval2[0]+1:
		# no intersection
		return None
	elif interval1[1] <= interval2[1]:
		# intersection exists without extending past the first interval
		if interval1[1] == interval2[0]:
			return interval1[1]
		else:
			return (interval2[0], interval1[1])
	else:
		# intersection exists with extending past the first interval
		if interval2[1] >= interval1[0]-1:
			# intersection is a single point
			return interval2[1]
		else:
			return (interval2[0], interval2[1])
