def max_depth(s: str) -> int:
    max_d = 0
    depth = 0
    for i in range(len(s)):

        if s[i] == "(":
            depth += 1
            if depth > max_d:
                max_d = depth

        if s[i] == ")":
            if depth > 0:
                depth -= 1
            else:
                return -1

    if depth != 0:
        return -1

    return max_d
