def max_depth(s):
    current_max = 0 # To store current count
    max = 0 # To store maximum count
    n = len(s)

    # Traverse the input string
    for i in range(n):
        if s[i] == '(':
            current_max += 1

            # Update max if required
            if current_max > max:
                max = current_max
        elif s[i] == ')':
            if current_max > 0:
                current_max -= 1
            else:
                return -1

    # If there is unbalanced parenthesis
    if current_max != 0:
        return -1

    return max
