def max_depth(s: str) -> int:
    max_d = 0
    current_max = 0
    n = len(s)
    for i in range(n):
        if s[i] == '(':
            current_max += 1
            if current_max > max_d:
                max_d = current_max
        elif s[i] == ')':
            if current_max > 0:
                current_max -= 1
            else:
                return -1
    if current_max != 0:
        return -1
    return max_d
