def deepest_nesting_level(input_string):
    max_level = 0
    current_level = 0
    for char in input_string:
        if char == '(':
            current_level += 1
            if current_level > max_level:
                max_level = current_level
        elif char == ')':
            if current_level > 0:
                current_level -= 1
            else:
                return -1
    if current_level != 0:
        return -1
    return max_level
