def caesar_cipher(input_string, shift):
    cipher_text = ""

    for i in input_string:
        if i.isalpha():
            stay_in_alphabet = shift % 26

            if i.isupper():
                new_unicode = ord(i) + stay_in_alphabet
                if new_unicode > ord('Z'):
                    new_unicode -= 26
                elif new_unicode < ord('A'):
                    new_unicode += 26
            elif i.islower():
                new_unicode = ord(i) + stay_in_alphabet
                if new_unicode > ord('z'):
                    new_unicode -= 26
                elif new_unicode < ord('a'):
                    new_unicode += 26

            new_character = chr(new_unicode)
            cipher_text += new_character
        else:
            cipher_text += i

    return cipher_text
