def caesar_cipher_encrypt(string, shift):
    encrypted_string = ""
    for char in string:
        if char.isalpha():
            unicode_offset = ord('a') if char.islower() else ord('A')
            encrypted_char = chr((ord(char) - unicode_offset + shift) % 26 + unicode_offset)
            encrypted_string += encrypted_char
        else:
            encrypted_string += char

    return encrypted_string
