from datetime import datetime
import pytz

def parse_iso8601(date_str, delimiter='T', timezone=None):
    """ 
    Return a UNIX timestamp from the given date.
    Delimiter corresponds to the time delimiter (e.g. 2023-10-19T00:00:00+0000)
    """

    try:
        if timezone is None:
            timezone = pytz.UTC
        else:
            timezone = pytz.timezone(timezone)

        date_time_obj = datetime.strptime(date_str, '%Y-%m-%d'+delimiter+'%H:%M:%S%z')
        date_time_obj = date_time_obj.astimezone(timezone)

        return str(int(date_time_obj.timestamp()))
    except Exception as e:
        return None
