from datetime import datetime

def parse_iso8601(date_str, delimiter='T', timezone=None):
    """ Return a UNIX timestamp from the given date.
        Delimiter corresponds to the time delimiter 
        (e.g. 2023-10-19T00:00:00+0000)
    """
    try:
        # If the timezone is specified, include it in the format
        if timezone:
            date_str += timezone
            date_format = "%Y-%m-%d"+delimiter+"%H:%M:%S%z"
        else:
            date_format = "%Y-%m-%d"+delimiter+"%H:%M:%S"

        dt = datetime.strptime(date_str, date_format)
        unix_timestamp = dt.timestamp()

        return str(int(unix_timestamp))

    except ValueError:
        # The date_str does not match the format
        return None
