from datetime import datetime
import pytz

def parse_iso8601(date_str, delimiter='T', timezone=None):
    """ 
    Return a UNIX timestamp from the given date.
    Delimiter corresponds to the time delimiter (e.g. 2023-10-19T00:00:00+0000)
    """
    try:
        date_object = datetime.strptime(date_str, '%Y-%m-%d{}%H:%M:%S%z'.format(delimiter))

        if timezone is not None:
            date_object = date_object.astimezone(pytz.timezone(timezone))

        return str(int(date_object.timestamp()))

    except ValueError:
        return None
