def count_permutations(n, m, k, p):
    C = [[0]*(n+1) for _ in range(n+1)]
    dp = [[0]*(n+1) for _ in range(n+1)]
    dp2 = [[0]*(n+1) for _ in range(n+1)]

    for i in range(n+1):
        C[i][0] = 1
        for j in range(1, i+1):
            C[i][j] = (C[i-1][j-1] + C[i-1][j]) % p

    dp[0][0] = dp2[0][0] = 1
    for i in range(1, n+1):
        dp[i][0] = dp2[i-1][0] * i % p
        for j in range(1, i+1):
            dp[i][j] = (dp2[i-1][j]*j + dp2[i-1][j-1]*(i-j+1)) % p
        for j in range(i+1):
            dp2[i][j] = (dp2[i-1][j] + dp[i][j]) % p

    ans = [0]*(k+1)
    for i in range(m, n+1):
        for j in range(max(0, k-(n-i)), min(i, k)+1):
            ans[k] = (ans[k] + C[n][i] * dp[i][j] % p * dp[n-i][k-j]) % p

    return ans[k]
