def min_difference(a, b, c):
    total_time = a + 2*b + 3*c
    max_time = total_time // 2
    dp = [0] * (max_time + 1)
    for song in [1]*a + [2]*b + [3]*c:
        for time in range(max_time, song-1, -1):
            dp[time] = max(dp[time], dp[time-song]+song)
    return abs(total_time - 2*dp[max_time])
