def min_difference(a, b, c):
    total_duration = a + 2 * b + 3 * c
    max_duration = total_duration // 2
    durations = [0]*(max_duration + 1)
    for k in range(1, a + 1):
        for i in range(max_duration, -1, -1):
            if i >= 1 and durations[i - 1] > durations[i]:
                durations[i] = durations[i - 1]
    for k in range(1, b + 1):
        for i in range(max_duration, 1, -1):
            if i >= 2 and durations[i - 2] > durations[i]:
                durations[i] = durations[i - 2]
    for k in range(1, c + 1):
        for i in range(max_duration, 2, -1):
            if i >= 3 and durations[i - 3] > durations[i]:
                durations[i] = durations[i - 3]
    return abs(total_duration - 2 * durations[-1])
