def min_difference(a, b, c):
    a, b, c = int(a), int(b), int(c)
    total_time = a + 2 * b + 3 * c
    target_time = total_time // 2

    dp = [0] * (target_time + 1)
    for song in [1]*a + [2]*b + [3]*c:
        for t in range(target_time, song-1, -1):
            dp[t] = max(dp[t], dp[t-song] + song)

    return abs(total_time - 2 * dp[-1])
