def minimal_concert_difference(a, b, c):
    total_time = a * 1 + b * 2 + c * 3
    half_time = total_time // 2
    max_time = [[0 for _ in range(half_time + 1)] for _ in range(a + b + c + 1)]
    durations = [1]*a + [2]*b + [3]*c
    for i in range(1, a + b + c + 1):
        for j in range(1, half_time + 1):
            if j >= durations[i - 1]:
                max_time[i][j] = max(max_time[i - 1][j], max_time[i - 1][j - durations[i - 1]] + durations[i - 1])
            else:
                max_time[i][j] = max_time[i - 1][j]
    return total_time - 2 * max_time[-1][-1]
