def minimal_difference(a, b, c):
    total_minutes = a + b*2 + c*3
    half_minutes = total_minutes // 2
    dp = [0] * (half_minutes + 1)

    for _ in range(a):
        for j in range(half_minutes, 0, -1):
            if j >= 1:
                dp[j] = max(dp[j], dp[j - 1] + 1)

    for _ in range(b):
        for j in range(half_minutes, 0, -1):
            if j >= 2:
                dp[j] = max(dp[j], dp[j - 2] + 2)

    for _ in range(c):
        for j in range(half_minutes, 0, -1):
            if j >= 3:
                dp[j] = max(dp[j], dp[j - 3] + 3)

    return abs(total_minutes - 2 * dp[half_minutes])
