def min_diff(a, b, c):
    a, b, c = map(int, (a, b, c))
    songs = [1]*a + [2]*b + [3]*c
    total = sum(songs)
    half = total // 2

    dp = [0] * (half+1)
    for i in range(len(songs)):
        for j in range(half, songs[i]-1, -1):
            dp[j] = max(dp[j], dp[j-songs[i]]+songs[i])

    return abs(total - 2*dp[half])
