def minimal_duration_difference(a: int, b: int, c: int) -> int:
    total_duration = a * 1 + b * 2 + c * 3

    dp = [0] * (total_duration // 2 + 1)
    for i in range(a + 1):
        for j in range(b + 1):
            for k in range(c + 1):
                dp[min(i + j * 2 + k * 3, total_duration // 2)] = max(dp[min(i + j * 2 + k * 3, total_duration // 2)], i + j * 2 + k * 3)

    return total_duration - dp[total_duration // 2] * 2
