def game_winner(A):
    n = len(A)
    l, r = 0, n-1
    last = -1
    turn = 0
    while (l <= r):
        if (A[l] > last and (A[l] < A[r] or A[r] <= last)) or (A[l] > last and r == l):
            last = A[l]
            l += 1
        elif A[r] > last:
            last = A[r]
            r -= 1
        else:
            break
        turn += 1
    if turn % 2 == 0:
        return 'Bob'
    else:
        return 'Alice'
