def determine_winner(A):
    N = len(A)
    Alice, Bob = 0, 1
    res = [['' for _ in range(N)] for _ in range(N)]
    for length in range(1, N+1):
        for i in range(N-length+1):
            j = i + length - 1
            if length == 1:
                res[i][j] = Alice
            else:
                if res[i+1][j] == res[i][j-1]:
                    res[i][j] = Alice if A[i] > A[j] else Bob
                elif res[i+1][j] < res[i][j-1]:
                    res[i][j] = Alice if A[i] > A[res[i+1][j]] else Bob
                else:
                    res[i][j] = Alice if A[j] > A[res[i][j-1]] else Bob

    return 'Alice' if res[0][N-1] == Alice else 'Bob'
