def determine_winner(A):
    n = len(A)
    left, right = 0, n - 1
    sequence = [0]
    turn = True  # True for Alice, False for Bob
    while left <= right:
        if A[right] < A[left] and A[right] > sequence[-1]:
            sequence.append(A[right])
            right -= 1
        elif A[right] >= A[left] and A[left] > sequence[-1]:
            sequence.append(A[left])
            left += 1
        elif A[right] > sequence[-1]:
            sequence.append(A[right])
            right -= 1
        elif A[left] > sequence[-1]:
            sequence.append(A[left])
            left += 1
        else:
            break
        turn = not turn
    return 'Alice' if turn else 'Bob'
