    alice_turn = True
    last_num = min(array) - 1
    while len(array) > 0 and (array[0] > last_num or array[-1] > last_num):
        if array[0] > array[-1] and array[0] > last_num:
            last_num = array.pop(0)
        elif array[-1] > last_num:
            last_num = array.pop(-1)
        elif array[0] > last_num:
            last_num = array.pop(0)
        alice_turn = not alice_turn

