def game_winner(A):
    i = 0
    j = len(A) - 1
    current = -1
    turn = 0
    while i <= j:
        if A[i] > current and (A[j] <= current or A[i] <= A[j]):
            current = A[i]
            i += 1
        elif A[j] > current:
            current = A[j]
            j -= 1
        else:
            break
        turn = 1 - turn
    return 'Alice' if turn else 'Bob'
