def gameWinner(array):
    alice_turn = True
    left = 0
    right = len(array) - 1
    sequence = [0]

    while left <= right:
        if array[left] > sequence[-1] and (array[left] <= array[right] or array[right] <= sequence[-1]):
            sequence.append(array[left])
            left += 1
        elif array[right] > sequence[-1] and (array[right] <= array[left] or array[left] <= sequence[-1]):
            sequence.append(array[right])
            right -= 1
        else:
            break

        alice_turn = not alice_turn

    if alice_turn:
        return 'Bob'
    else:
        return 'Alice'
