def beautiful_coloring(arr):
    #sort the array in descending order.
    arr.sort(reverse = True)
    # check adjacency for every divisor of maximum number in the array.
    for d in range(1, arr[0] + 1):
        beautiful = True
        color = -1
        for num in arr:
            if num % d == 0:
                if color == 0:
                    beautiful = False
                    break
                else:
                    color = 0
            else:
                if color == 1:
                    beautiful = False
                    break
                else:
                    color = 1
        if beautiful:
            return d
    return 0
