def find_ba_string(k, x, s):
    asterisks = s.count('*')
    dp = [[0]*(asterisks+1) for _ in range(asterisks+1)]
    dp[0][0] = 1
    for i in range(1, asterisks+1):
        dp[i][0] = dp[i-1][0]*(k+1)
        for j in range(1, i+1):
            dp[i][j] = dp[i-1][j-1] + dp[i-1][j]*(k+1)
    res = ''
    i = asterisks
    j = 0
    for c in s:
        if c == '*':
            if dp[i-1][j]*(k+1) >= x:
                i -= 1
            else:
                x -= dp[i-1][j]*(k+1)
                res += 'b'*(x//dp[i-1][j])
                x %= dp[i-1][j]
                res += 'a'
                i -= 1
                j += 1
        else:
            res += c
    return res+'b'*(x//max(1, dp[i][j]))
