def get_intersection(interv1, interv2):
    interv1 = eval(interv1)
    interv2 = eval(interv2)
    min_val = max(interv1[0], interv2[0])
    max_val = min(interv1[1], interv2[1])

    if min_val < max_val:
        return (min_val, max_val)
    elif min_val == max_val:
        return min_val
    else:
        return None
