def interval_intersection(interval1, interval2):
    interval1 = eval(interval1)
    interval2 = eval(interval2)
    intersection_start = max(interval1[0], interval2[0])
    intersection_end = min(interval1[1], interval2[1])
    if intersection_start < intersection_end:
        return (intersection_start, intersection_end)
    elif intersection_start == intersection_end:
        return intersection_start
    else:
        return None
