def intersection(int1, int2):
    int1 = eval(int1)
    int2 = eval(int2)
    intersect = max(int1[0], int2[0]), min(int1[1], int2[1])

    if intersect[0] > intersect[1]:
        return None
    elif intersect[0] == intersect[1]:
        return intersect[0]
    else:
        return intersect
