def convert_base(num, base):
    if base == 2:
        return bin(num).replace("0b", "")
    elif base == 8:
        return oct(num).replace("0o", "")
    elif base == 16:
        return hex(num).replace("0x", "")
    else:
        conversion = ""
        while num > 0:
            conversion = str(num % base) + conversion
            num //= base
        return conversion
