# Decimal to Other Base
def convert_to_base(dec_num, base):
    if dec_num == 0:
        return '0'
    nums = []
    while dec_num:
        dec_num, remainder = divmod(dec_num, base)
        nums.append(str(remainder))
    return ''.join(reversed(nums))
