def max_depth(s: str) -> int:
    max_d = 0
    current_depth = 0
    for char in s:
        if char == '(':
            current_depth += 1
            if current_depth > max_d:
                max_d = current_depth
        elif char == ')':
            if current_depth > 0:
                current_depth -= 1 
            else:
                return -1
    if current_depth != 0:
        return -1

    return max_d
