def caesar_cipher(text, shift):
    encrypted_text = ""
    for char in text:
        if char.isalpha():
            ascii_val = ord(char)
            ascii_shifted = ascii_val + int(shift)
            if char.isupper():
                if ascii_shifted > ord('Z'):
                    ascii_shifted -= 26
            else:
                if ascii_shifted > ord('z'):
                    ascii_shifted -= 26
            encrypted_text += chr(ascii_shifted)
        else:
            encrypted_text += char
    return encrypted_text
