def caesar_cipher(input_string: str, shift: int) -> str:
    encrypted_string = ''
    for character in input_string:
        ascii_val = ord(character)
        new_val = ascii_val + shift
        if 'a' <= character <= 'z':
            if new_val > ord('z'):
                new_val -= 26
        elif 'A' <= character <= 'Z':
            if new_val > ord('Z'):
                new_val -= 26
        encrypted_string += chr(new_val)
    return encrypted_string
