from datetime import datetime, timezone

def parse_iso8601(date_str, delimiter='T', timezone=None):
    """ Return a UNIX timestamp from the given date.
        Delimiter corresponds to the time delimiter (e.g. 2023-10-19T00:00:00+0000)
    """
    if "Z" in date_str: 
        date_str = date_str.replace("Z", "+0000")

    try:
        date = datetime.strptime(date_str, '%Y-%m-%d{}%H:%M:%S%z'.format(delimiter))
        timestamp = int(date.replace(tzinfo=timezone.utc).timestamp())
        return str(timestamp)
    except ValueError:
        return None
