def count_permutations(n, m, k, p):
    dp = [[[[0 for _ in range(102)] for _ in range(102)] for _ in range(52)] for _ in range(52)]
    dp[0][0][0][0] = 1
    mod = p

    for i in range(n):
        for j in range(min(i, m) + 1):
            for k in range(min(i, k) + 1):
                for l in range(min(i, k) + 1):
                    if dp[i][j][k][l]:
                        for x in [0, 1]:
                            nj = j + 1 if x else j
                            if nj > m: continue

                            nk = k + 1 if (x and l == k) else k
                            if nk > m: continue

                            nl = l + 1 if x else max(l - 1, 0)

                            dp[i + 1][nj][nk][nl] = (dp[i + 1][nj][nk][nl] + dp[i][j][k][l]) % mod

    return dp[n][m][k][min(m, k)] % mod
