def count_permutations(n, m, k, p):
    fact = [1]*5005
    dp = [[[0]*55 for _ in range(55)] for _ in range(2)]
    inv = [1]*5005

    for i in range(1,5005):
        fact[i] = (fact[i-1]*i)%p
        inv[i] = pow(fact[i],p-2,p)

    dp[0][0][0] = 1
    fl = 0

    for i in range(k):
        fl^=1
        for j in range(min(i+1,m+1)):
            for k in range(j+1):
                dp[fl][j][k] = dp[fl^1][j][k]
                if j:
                    dp[fl][j][k] = (dp[fl][j][k] + dp[fl^1][j-1][k]*fact[n-i]%p*inv[n-i-j+1])%p
                if k and j:
                    dp[fl][j][k] = (dp[fl][j][k] + dp[fl^1][j][k-1]*fact[n-i]%p*inv[m-j+2]*inv[n-i-j+1])%p
                if j < i+1 and k < j+1:
                    dp[fl][j][k] = (dp[fl][j][k] + dp[fl^1][j][k]*fact[n-i]%p*inv[m-j+1]*inv[j-k])%p

    ans = sum(dp[fl][m][i]*fact[n-i]%p for i in range(k+1))
    return(ans%p)
