def count_good_permutations(n, m, k, p):
    dp1 = [[[0]*305 for _ in range(305)] for _ in range(305)]
    dp2 = [[[0]*305 for _ in range(305)] for _ in range(305)]
    fac = [0]*305
    rev = [0]*305
    fac[0] = 1
    for i in range(1, 305):
        fac[i] = fac[i-1]*i%p
    rev[0] = rev[1] = 1
    for i in range(2, 305):
        rev[i] = -(p/i)*rev[p%i]%p
    for i in range(2, 305):
        rev[i] = rev[i]*rev[i-1]%p
    dp1[0][0][0] = 1
    for i in range(1,n+1):
        for j in range(i+1):
            for t in range(j+1):
                if dp1[i-1][j][t]:
                    dp1[i][j+1][t+1] += dp1[i-1][j][t]
                    dp1[i][j+1][t+1] %= p
                    dp1[i][j][t] += dp1[i-1][j][t]*(j-t+1)
                    dp1[i][j][t] %= p
                    if j != t:
                        dp1[i][j][t+1] += dp1[i-1][j][t]*(j-t+1)
                        dp1[i][j][t+1] %= p
                    dp1[i][j][t+1] += dp1[i-1][j][t]*(j-t+1)
                    dp1[i][j][t+1] %= p
    dp2[0][0][0] = 1
    for i in range(1, n+1):
        for j in range(i+1):
            for t in range(j+1):
                dp2[i][j][t] = dp2[i-1][j][t]
                if j != t:
                    dp2[i][j][t] += dp2[i-1][j][t]
                    dp2[i][j][t] %= p
                if i != j:
                    dp2[i][j][t+1] += dp2[i-1][j][t]*2
                    dp2[i][j][t+1] %= p
                    if j != t:
                        dp2[i][j][t+1] += dp2[i-1][j][t]*(j-t+1)*2
                        dp2[i][j][t+1] %= p
    ans = 0
    for i in range(k+1):
        for j in range(i+1):
            for s1 in range(2):
                for s2 in range(2):
                    if s1 + s2 > m:
                        continue
                    if (s1 == 0 or i) and (s2 == 0 or i-j):
                        ans += dp1[n][i][j]*dp2[n][k-i][k-i-j]%p*fac[k]%p*rev[k-i]%p
                        ans %= p
    return ans

