def count_permutations(n, m, k, p):
    f = [[0] * (n + 10) for _ in range(n + 10)]
    g = [[0] * (n + 10) for _ in range(n + 10)]

    f[0][0] = 1
    for i in range(1, n + 1):
        f[i][0] = (f[i - 1][0] * i) % p
        for j in range(1, i + 1):
            f[i][j] = (f[i - 1][j - 1] + f[i - 1][j] * j) % p

    g[0][0] = 1
    for i in range(1, m + 1):
        g[i][0] = (g[i - 1][0] * i) % p
        for j in range(1, i + 1):
            g[i][j] = (g[i - 1][j - 1] + g[i - 1][j] * j) % p

    res = 0
    for i in range(max(1, m - n + k), min(m, k) + 1):
        for j in range(i, min(m, k) + 1):
            if (j - i) % 2 == (k % 2):
                tmp = f[n][i] * g[m - 1][j] % p
                tmp = tmp * f[m + n - j - 1][k - 1] % p
                res = (res + tmp) % p
    return res

