def min_concert_diff(a, b, c):
    total_duration = a + b*2 + c*3
    half_duration = total_duration // 2
    durations = [0] + [0]*half_duration
    for i in range(a):
        for j in range(half_duration, -1, -1):
            if j >= 1:
                durations[j] = max(durations[j], durations[j - 1] + 1)
    for i in range(b):
        for j in range(half_duration, -1, -1):
            if j >= 2:
                durations[j] = max(durations[j], durations[j - 2] + 2)
    for i in range(c):
        for j in range(half_duration, -1, -1):
            if j >= 3:
                durations[j] = max(durations[j], durations[j - 3] + 3)
    return abs(total_duration - 2*durations[half_duration])
