def minimal_difference(a, b, c):
    total = a + 2*b + 3*c
    mins = total // 2

    dp = [0]*(mins+1)
    for k in range(a):
        for i in range(mins, 0, -1):
            if i > 0:
                dp[i] = max(dp[i], dp[i-1]+1)
    for k in range(b):
        for i in range(mins, 1, -1):
            if i > 1:
                dp[i] = max(dp[i], dp[i-2]+2)
    for k in range(c):
        for i in range(mins, 2, -1):
            if i > 2:
                dp[i] = max(dp[i], dp[i-3]+3)

    return total - 2*dp[mins]
