def jigven_xor(n, a):
    n = len(a)
    x = [0]*(1<<n)
    y = []
    result = []
    for i in range(n):
        y.append(a[i])
        for j in range(len(y)):
            x[y[j] | a[i]] = 1
            y[j] |= a[i]
    for i in range(1<<n):
        if x[i] == 1:
           result.append(i)
    return sorted(result)

