def game_winner(A):
    l = 0
    r = len(A) - 1
    last = -1
    alice_turn = True
    while l <= r:
        if A[l] > last and (A[r] <= last or A[l] <= A[r]):
            last = A[l]
            l += 1
        elif A[r] > last and (A[l] <= last or A[r] < A[l]):
            last = A[r]
            r -= 1
        else:
            break
        alice_turn = not alice_turn
    return 'Alice' if not alice_turn else 'Bob'
