def game_winner(array):
    left = 0
    right = len(array) - 1
    sequence_end = -1
    turn = 0
    while left <= right:
        if array[left] <= sequence_end and array[right] <= sequence_end:
            break
        if turn % 2 == 0:
            if array[right] > sequence_end and (array[left] <= sequence_end or array[right] >= array[left]):
                sequence_end = array[right]
                right -= 1
            else:
                sequence_end = array[left]
                left += 1
        else:
            if array[left] > sequence_end and (array[right] <= sequence_end or array[left] >= array[right]):
                sequence_end = array[left]
                left += 1
            else:
                sequence_end = array[right]
                right -= 1
        turn += 1
    if turn % 2 == 0:
        return 'Bob'
    else:
        return 'Alice'
