def game_winner(A):
    left, right = 0, len(A) - 1
    sequence = []
    turn = 0  # Alice
    winner = ''

    while left <= right:
        if len(sequence) == 0:
            if A[left] > A[right]:
                sequence.append(A[right])
                right -= 1
            else:
                sequence.append(A[left])
                left += 1
            continue
        if sequence[-1] < A[left] and sequence[-1] < A[right]:
            if A[left] > A[right]:
                sequence.append(A[right])
                right -= 1
            else:
                sequence.append(A[left])
                left += 1
        elif sequence[-1] < A[left]:
            sequence.append(A[left])
            left += 1
        elif sequence[-1] < A[right]:
            sequence.append(A[right])
            right -= 1
        else:
            break
        turn = 1 - turn
    if turn == 0:
        winner = 'Bob'
    else:
        winner = 'Alice'

    return winner
