def find_winner(A):
    left = 0
    right = len(A) - 1
    last = -1
    turn = True  # True for Alice, False for Bob
    while left <= right:
        if min(A[left], A[right]) > last:
            last = min(A[left], A[right])
            if A[left] == last:
                left += 1
            else:
                right -= 1
        elif max(A[left], A[right]) > last:
            last = max(A[left], A[right])
            if A[left] == last:
                left += 1
            else:
                right -= 1
        else:
            break
        turn = not turn
    return 'Alice' if turn else 'Bob'
