def winner_of_game(A):
    left = 0
    right = len(A) - 1
    sequence_so_far = -1
    turn = 0
    while left <= right:
        if A[left] > sequence_so_far and (A[right] <= sequence_so_far or A[left] > A[right]):
            sequence_so_far = A[left]
            left += 1
        elif A[right] > sequence_so_far and (A[left] <= sequence_so_far or A[right] >= A[left]):
            sequence_so_far = A[right]
            right -= 1
        else:
            break
        turn += 1
    if turn % 2 == 0:
        return 'Bob'
    else:
        return 'Alice'
