def determine_winner(A):
    start, end = 0, len(A)-1
    current_num= -1
    alice_turn = True

    while start <= end:
        if A[start] > current_num and (A[start] <= A[end] or A[end] <= current_num):
            current_num = A[start]
            start += 1
        elif A[end] > current_num and (A[end] <= A[start] or A[start] <= current_num):
            current_num = A[end]
            end -= 1
        else:
            if alice_turn:
                return 'Bob'
            else:
                return 'Alice'
        alice_turn = not alice_turn

    # If all numbers in array was used (sequence array was built successfully)
    if alice_turn:
        return 'Bob'
    else:
        return 'Alice'
