def beautiful_coloring(arr):
    n = len(arr)
    arr.sort()
    if n == 1:
        return arr[0]

    d = arr[1] - arr[0]
    for i in range(1, n):
        d = min(d, arr[i] - arr[i-1])

    for i in range(n - 1):
        if arr[i] % d == arr[i + 1] % d:
            return 0
    return d

